import React, {useReducer} from 'react'
import GuestContext from './guestContext'
import guestReducer from './guestReducer'
import {
    TOGGLE_FILTER
} from './../type'

const GuestState = (props) => {
    const initialState = {
        filterGuests: false,
        guests:[
            {
                id: 1,
                name: "Jack Smith",
                phone: "333 333 3343",
                dietary: "Vegan",
                isConfirmed: false
            },
            {
                id: 2,
                name: "Jacky Brown",
                phone: "333 333 3343",
                dietary: "Vegan",
                isConfirmed: true
            },
            {
                id: 3,
                name: "Bob Belcher",
                phone: "333 333 3343",
                dietary: "Non-Veg",
                isConfirmed: false
            },
        ]
    }

    const [state, dispatch] = useReducer(guestReducer, initialState)


   //very first action, does a dispatch
   const toggleFilter = () => {
    console.log(state.filterGuests)
       dispatch({
           type: TOGGLE_FILTER
       })
   }


    return (
        <GuestContext.Provider
            value={{
                guests: state.guests,
                toggleFilter
            }}
        >{props.children}</GuestContext.Provider>
    )
}


export default GuestState
