import {
    TOGGLE_FILTER
} from './../type'

export default (state, {type, payload}) => {
    switch(type){
        case TOGGLE_FILTER:
            return{
                ...state, //cant mutate state directly so we make a copy of it...
                filterGuest: !state.filterGuest
            }
        default:
            return state
    }
}
